    public List<Integer> findSubstring(String s, String[] words) {
        List<Integer> list = new ArrayList<>();

        //for (int i = 0; i < words.length; i++) {
        //    String item = words[i];
        //
        //    int from = s.indexOf(item, 0);
        //    if(from < 0) {
        //        return Collections.emptyList();
        //    }
        //    while (from > -1){
        //        list.add(from);
        //        from = s.indexOf(item, from + 1);
        //    }
        //}

        for (int i = 0; i < s.length(); i++) {
            for (int j = 0; j < words.length; j++) {
                if(s.indexOf(words[j], i)==i){
                    list.add(i);
                    break;
                }
            }
        }

        //将问题转化为在有序列表中寻找满足条件（长度为length个的等差数列）的子列表
        Collections.sort(list);

        int unit = words[0].length();
        int nums = words.length;

        List<Integer> indexs = new ArrayList<>();
        for (int i = 0; i + nums <= list.size(); i++) {
            int index = list.get(i);

            boolean contains = true;
            for (int j = 1;j < nums; j++) {
                int offer = j * unit;
                if(list.get(i + j) != index + offer){
                    contains = false;
                    break;
                }
            }
            if(contains){
                indexs.add(index);
            }
        }
        return indexs;
    }

